import React from 'react';
import red from './red.png'
import blue from './blue.png'
import blank from './blank.png'
let animationRunning = false

const Square = (props) => {

  const handleClick = () => {
    animationRunning = false
    let column = 0;
    const {id, handleClick, topSquare} = props
    if(topSquare){
      column = id
    }
    let modId = id + 7;
    for(let i = 0; i < 7; i++){
      column = i;
      if((modId-i)%7 == 0){
        break;
      }
    }
    return handleClick(column);
  }

  const getClass = () => {
    if(!props.topSquare){
      return "square"
    }
    return animationRunning ? "animationSquare" : "hoverSquare"
  }

  const hoverOver = () => {
    const {id, hoverOver, topSquare} = props
    let column = 0
    if(topSquare){
      column = id
    }
    else {
      let modId = id + 7;
      column = 0;
      for(let i = 0; i < 7; i++){
        column = i;
        if((modId-i)%7 == 0){
          break;
        }
      }
    }
    return hoverOver(column)
  }

  const getPiece = () => {
    const{value, topSquare, userOneTurn, hoverColumn, id} = props
    if(topSquare){
      return id != hoverColumn ? blank : userOneTurn ? red : blue
    }
    else {
      if(value === 1){
        return red
      }
      else if(value === 2){
        return blue
      }
      return blank
    }
  }

  const runAnimation = () => {
    animationRunning = true
    console.log("run")
    return props.runAnimation()
  }

  return (
      <div className = {getClass()} onClick={runAnimation} onMouseOver = {hoverOver} onAnimationEnd={handleClick}>
        <img className = "face" src = {getPiece()} alt = {blank}></img>
      </div>
  );
}

export default Square;
