import React from 'react';
import red from './red.png'
import blue from './blue.png'
import blank from './blank.png'


const Square = (props) => {

  const handleClick = () => {
    const {id, handleClick, canClick} = props
    if(!canClick){
      return;
    }
    let modId = id + 7;
    let column = 0;
    for(let i = 0; i < 7; i++){
      column = i;
      if((modId-i)%7 == 0){
        break;
      }
    }
    return handleClick(column);
  }

  const getClass = () => {
    if(props.canClick){
      return "square";
    }
    return "topSquare";
  }

  return (
      <div className = {props.canClick ? "square" : "topSquare"} onClick={handleClick}>
        <img className = "face" src = {props.value==2 ? blue : props.value ==1 ? red : blank } alt = {blank}></img>
      </div>
  );
}

export default Square;
