import React from 'react';
import red from './red.png'
import blue from './blue.png'
import blank from './blank.png'


const Square = (props) => {

  const handleClick = () => {
    const {id, handleClick, topSquare} = props
    if(!topSquare){
      return;
    }
    let modId = id + 7;
    let column = 0;
    for(let i = 0; i < 7; i++){
      column = i;
      if((modId-i)%7 == 0){
        break;
      }
    }
    return handleClick(column);
  }

  const getClass = () => {
    if(props.topSquare){
      return "square"
    }
    return "hoverSquare"
  }

  const hoverOver = () => {
    const {id, handleClick, topSquare} = props
    let column = 0
    if(topSquare){
      column = id
    }
    else {
      let modId = id + 7;
      column = 0;
      for(let i = 0; i < 7; i++){
        column = i;
        if((modId-i)%7 == 0){
          break;
        }
      }
    }
    return hoverOver(column)
  }

  return (
      <div className = {getClass()} onClick={handleClick}>
        <img className = "face" src = {props.value==2 ? blue : props.value ==1 ? red : blank } alt = {blank}></img>
      </div>
  );
}

export default Square;
