import React, { useState } from 'react';
import Square from "./Square.js";
import wc from './WinningConditions.js';
let allPlays = Array(42).fill(0);
let winConditions = []
let userOnePlays = []
let userTwoPlays = []
let gameOver = false
let userOneTurn = false

const Board = () => {

  function checkIfWinner(){
    if(winConditions.length == 0){
      winConditions = wc.getAllWinConditions();
    }

    if(this.checkIfUserIsWinner(1)){
      gameOver = true
      alert("User One Wins!")
    }
    else if(this.checkIfUserIsWinner(2)){
      gameOver = true
      alert("User Two Wins!")
    }
  }

  function checkIfUserIsWinner (user){
    let userPlays = user == 1 ? userOnePlays : userTwoPlays;
    let userWins = false;
    for(let i = 0; i < winConditions.length; i++){
      let curWinCondition = winConditions[i];
      userWins = true;
      for(let j = 0; j < 4; j++){
        if(!userPlays.includes(curWinCondition[j])){
          userWins = false;
          break;
        }
      }
      if(userWins){
        break;
      }
    }
    return userWins;
  }

  const handleClick = (column) => {
    if(allPlays[column] != 0){
      return;
    }
    if(gameOver){
      return;
    }
    for(let i = 0; i < 6; i++){
      let index = column + i * 7;
      if(index === 0){
        allPlays[index] = userOneTurn ? 1 : 2;
        if(userOneTurn){
          userOnePlays.push(index)
        }
        else{
          userTwoPlays.push(index)
        }
        break;
      }
    }
    this.checkIfWinner();
    if(!gameOver){
      userOneTurn = !userOneTurn
    }
  }

  function resetGame (){
    gameOver = false
    userOnePlays = []
    userTwoPlays = []
    allPlays = Array(42).fill(0)
    userOneTurn = true
  }

  return (
    <div className = "backgroundStuff">
      <h1 className = "glow">Connect Four</h1>
      <button onClick = {resetGame()} className = "resetButton">Reset</button>
      <div className = "grid">
        {allPlays.map((element,i) =>{
            return(
              <Square key = {i.toString()} id = {i} value = {element} handleClick = {handleClick()}  />)
          })}
      </div>
    </div>
  );
}

export default Board;
